import React from 'react';
import './Tile.css';
import { useDrag } from 'react-dnd';

export default function Pieces({ image }) {
    const [{isDragging},drag] = useDrag(()=>{
        type:"piece"
    })
    return (
        <div style={{ backgroundImage: `url(${image})` }} className="chess-piece"></div>
    )
}
