import React, { useRef, useState, useEffect } from 'react'
import './GameBoard.css'
import Tile from './Tile';
import Referee from './Referee';
import { GRID_SIZE, horizontalAxis, verticalAxis } from '../constants';
import io from 'socket.io-client';

export let piece = {
  image: String,
  x: Number,
  y: Number,
  type: String,
  team: String,
  enPassant: Boolean
}

// array which holds the information regarding initial chess pieces and their 
const initialBoardStateWhite = [];

const initialBoardStateBlack = [];


for (let p = 0; p < 2; p++) {
  const teamType = (p === 0) ? "opponent" : "our";
  const type = (!false) ? ((p === 0) ? "B" : "W") : ((p === 0) ? "W" : "B");
  const pawns_x_cor = (p === 0) ? 1 : 6;
  const main_piece_x_cor = (p === 0) ? 0 : 7;
  //pawns
  for (let i = 0; i < 8; ++i) {
    initialBoardStateWhite.push({ image: `assets/images/${type}_pawn.png`, x: pawns_x_cor, y: i, type: "pawn", team: teamType });
  }
  //rooks
  initialBoardStateWhite.push({ image: `assets/images/${type}_rook.png`, x: main_piece_x_cor, y: 0, type: "rook", team: teamType });
  initialBoardStateWhite.push({ image: `assets/images/${type}_rook.png`, x: main_piece_x_cor, y: 7, type: "rook", team: teamType });
  //knights
  initialBoardStateWhite.push({ image: `assets/images/${type}_knight.png`, x: main_piece_x_cor, y: 1, type: "knight", team: teamType });
  initialBoardStateWhite.push({ image: `assets/images/${type}_knight.png`, x: main_piece_x_cor, y: 6, type: "knight", team: teamType });
  //bishops
  initialBoardStateWhite.push({ image: `assets/images/${type}_bishop.png`, x: main_piece_x_cor, y: 2, type: "bishop", team: teamType });
  initialBoardStateWhite.push({ image: `assets/images/${type}_bishop.png`, x: main_piece_x_cor, y: 5, type: "bishop", team: teamType });
  //kings
  initialBoardStateWhite.push({ image: `assets/images/${type}_king.png`, x: main_piece_x_cor, y: 4, type: "king", team: teamType });
  //queens
  initialBoardStateWhite.push({ image: `assets/images/${type}_queen.png`, x: main_piece_x_cor, y: 3, type: "queen", team: teamType });
}

for (let p = 0; p < 2; p++) {
  const teamType = (p === 0) ? "opponent" : "our";
  const type = (!true) ? ((p === 0) ? "B" : "W") : ((p === 0) ? "W" : "B");
  const pawns_x_cor = (p === 0) ? 1 : 6;
  const main_piece_x_cor = (p === 0) ? 0 : 7;
  //pawns
  for (let i = 0; i < 8; ++i) {
    initialBoardStateBlack.push({ image: `assets/images/${type}_pawn.png`, x: pawns_x_cor, y: i, type: "pawn", team: teamType });
  }
  //rooks
  initialBoardStateBlack.push({ image: `assets/images/${type}_rook.png`, x: main_piece_x_cor, y: 0, type: "rook", team: teamType });
  initialBoardStateBlack.push({ image: `assets/images/${type}_rook.png`, x: main_piece_x_cor, y: 7, type: "rook", team: teamType });
  //knights
  initialBoardStateBlack.push({ image: `assets/images/${type}_knight.png`, x: main_piece_x_cor, y: 1, type: "knight", team: teamType });
  initialBoardStateBlack.push({ image: `assets/images/${type}_knight.png`, x: main_piece_x_cor, y: 6, type: "knight", team: teamType });
  //bishops
  initialBoardStateBlack.push({ image: `assets/images/${type}_bishop.png`, x: main_piece_x_cor, y: 2, type: "bishop", team: teamType });
  initialBoardStateBlack.push({ image: `assets/images/${type}_bishop.png`, x: main_piece_x_cor, y: 5, type: "bishop", team: teamType });
  //kings
  initialBoardStateBlack.push({ image: `assets/images/${type}_king.png`, x: main_piece_x_cor, y: 4, type: "king", team: teamType });
  //queens
  initialBoardStateBlack.push({ image: `assets/images/${type}_queen.png`, x: main_piece_x_cor, y: 3, type: "queen", team: teamType });
}

//the url where the web socket server is listening for connections
const SOCKET_URL = `ws://192.168.29.13:8085/`;

// global socket object to use for communication with the server
const socket = io(SOCKET_URL);


export default function GameBoard({ room, isBlack }) {
  // now for our component we need to join the room , so we send a socketevent which will indicate that we join the given room
  // we will also send a socket event to leave the joined room upon component unmount
  useEffect(() => {
    socket.emit('joinRoomEvent', room);
    return () => {
      socket.emit('leaveRoomEvent', room);
    }
  }, []);






  const [numClients,setNumClients] = useState(0);
  const [activePiece, setActivePiece] = useState(null);
  const [gridX, setGridX] = useState(0);
  const [gridY, setGridY] = useState(0);
  // const [pieces, setPieces] = useState(isBlack ? initialBoardStateBlack : initialBoardStateWhite);
  const [pieces, setPieces] = useState([]);
  const chessBoardRef = useRef(null);
  const pawnPromotionModalRef = useRef(null);
  const referee = new Referee();
  const [promotionPawn, setPromotionPawn] = useState(null);
  const gameStartButtonRef = useRef(null);


  useEffect(()=>{
    setInterval(()=>{
      fetch('http://192.168.29.13:8080/api/num_clients', {
        method: "POST",
        headers: {
            "Content-type": "application/json",
        },
        body: room
    })
        .then(response => response.json())
        .then(data => {
            setNumClients(data["num_clients"]);
        })
        .catch(error => console.error('Error:', error));
    },3000);
  },[]);

  // socket.emit('clientToServer', JSON.stringify({ "pieceArr": pieces, "team": isBlack ? "black" : "white" }));


  socket.on("initial_state", (data) => {
    setPieces(JSON.parse(data));
  })
  socket.on("serverToClient", (data) => {
    console.log(data);
  })

  function startGameHandler(){
    alert(numClients)
    if(numClients != 2)
    alert('Please wait for the other player to join');
    else
    alert('you can start playing the game now')
  }

  function grabPiece(e) {
    const chessboard = chessBoardRef.current;
    const element = (e.target);
    if (element.classList.contains("chess-piece") && chessboard) {
      setGridX(Math.floor((e.clientX - chessboard.offsetLeft) / GRID_SIZE));
      setGridY(Math.floor((e.clientY - chessboard.offsetTop) / GRID_SIZE));
      const x = e.clientX;
      const y = e.clientY;
      element.style.position = "absolute";
      element.style.left = `${x - GRID_SIZE / 2}px`;
      element.style.top = `${y - GRID_SIZE / 2}px`;
      setActivePiece(element);
    }
  }

  function movePiece(e) {
    const chessboard = chessBoardRef.current;
    if (activePiece && chessboard) {
      const minX = chessboard.offsetLeft;
      const minY = chessboard.offsetTop;
      const maxX = chessboard.offsetLeft + chessboard.clientWidth;
      const maxY = chessboard.offsetTop + chessboard.clientHeight;
      const x = e.clientX;
      const y = e.clientY;
      activePiece.style.position = "absolute";

      if (x < minX) {
        activePiece.style.left = `${minX - GRID_SIZE / 2}px`;
      }
      else if (x > maxX) {
        activePiece.style.left = `${maxX - GRID_SIZE / 2}px`;
      }
      else activePiece.style.left = `${x - GRID_SIZE / 2}px`;

      if (y < minY) {
        activePiece.style.top = `${minY - GRID_SIZE / 2}px`;
      }
      else if (y > maxY) {
        activePiece.style.top = `${maxY - GRID_SIZE / 2}px`;

      }
      else activePiece.style.top = `${y - GRID_SIZE / 2}px`;
    }
  }

  function dropPiece(e) {
    const chessboard = chessBoardRef.current;
    if (activePiece && chessboard) {
      const x = Math.floor((e.clientX - chessboard.offsetLeft) / GRID_SIZE);
      const y = Math.floor((e.clientY - chessboard.offsetTop) / GRID_SIZE);
      const currentPiece = pieces.find(p => p.y === gridX && p.x === gridY);
      if (currentPiece) {
        const validMove = referee.isValidMove(gridY, gridX, y, x, currentPiece.type, currentPiece.team, pieces);
        const isEnPassantMove = referee.isEnPassantMove(gridY, gridX, y, x, currentPiece.type, currentPiece.team, pieces);
        const pawnDirection = "our" === currentPiece.team ? 1 : -1;
        if (isEnPassantMove) {
          const updatedPieces = pieces.reduce((results, piece) => {
            if (currentPiece === piece) {
              piece.enPassant = false;
              piece.x = y;
              piece.y = x;
              results.push(piece);
            }
            else if (!(piece.x === y + pawnDirection && piece.y === x)) {
              if ("pawn" === piece.type)
                piece.enPassant = false;
              results.push(piece);
            }
            return results;
          }, []);
          setPieces(updatedPieces);
          socket.emit('clientToServer', JSON.stringify({ "pieceArr": pieces, "team": isBlack ? "black" : "white" }));
        }
        else {
          if (validMove) {
            if ("pawn" === currentPiece.type) {
              const updatedPieces = pieces.reduce((results, piece) => {
                if (currentPiece === piece) {
                  if (piece.type === "pawn" && Math.abs(y - gridY) === 2) {
                    piece.enPassant = true;
                  }
                  else {
                    piece.enPassant = false;
                  }
                  piece.x = y;
                  piece.y = x;
                  if (0 === piece.x || 7 === piece.x) {
                    setPromotionPawn(currentPiece);
                    pawnPromotionModalRef.current.classList.remove('hidden');
                  }
                  results.push(piece);
                }
                else if (!(piece.x === y && piece.y === x)) {
                  if ("pawn" === piece.type)
                    piece.enPassant = false;
                  results.push(piece);
                }
                return results;
              }, []);
              setPieces(updatedPieces);
              socket.emit('clientToServer', JSON.stringify({ "pieceArr": updatedPieces, "team": isBlack ? "black" : "white" }));
            }
            else if ("knight" === currentPiece.type || "bishop" === currentPiece.type || "rook" === currentPiece.type || "queen" === currentPiece.type || "king" === currentPiece.type) {
              const updatedPieces = pieces.reduce((results, piece) => {
                if (currentPiece === piece) {
                  piece.x = y;
                  piece.y = x;
                  results.push(piece);
                }
                else if (!(piece.x === y && piece.y === x)) {
                  results.push(piece);
                }
                return results;
              }, []);
              setPieces(updatedPieces);
              socket.emit('clientToServer', JSON.stringify({ "pieceArr": updatedPieces, "team": isBlack ? "black" : "white" }));
            }

            // if (referee.inCheck(pieces)) {
            //   console.log("Check");
            //   setOppInCheck(true);
            //   socket.emit("clientToServerCheck", "Check");
            // }
          }
          else {
            // RESETS  THE PIECE POSITION
            activePiece.style.position = "relative";
            activePiece.style.removeProperty('top');
            activePiece.style.removeProperty('left');
          }
        }

      }
      setActivePiece(null);
    }
  }

  function promotePawn(type) {
    if (promotionPawn) {
      const updatedPieces = pieces.reduce((results, piece) => {
        if (promotionPawn === piece) {
          piece.type = type;
          piece.image = (!isBlack) ? ((piece.team === "our") ? `/assets/images/W_${type}.png` : `/assets/images/B_${type}.png`) : ((piece.team === "our") ? `/assets/images/B_${type}.png` : `/assets/images/W_${type}.png`);
        }
        results.push(piece);
        return results;
      }, []);
      setPieces(updatedPieces);
      socket.emit('clientToServer', JSON.stringify({ "pieceArr": updatedPieces, "team": isBlack ? "black" : "white" }));
      pawnPromotionModalRef.current.classList.add('hidden');
    }
  }

  function promotionTeamType() {
    if (promotionPawn)
      return (!isBlack) ? (promotionPawn.team === "our" ? "W" : "B") : (promotionPawn.team === "our" ? "B" : "W");
  }

  let board = [];
  for (let i = 0; i < verticalAxis.length; ++i) {
    for (let j = 0; j < horizontalAxis.length; ++j) {
      let image = undefined;
      pieces.forEach(p => { if (p.x === i && p.y === j) image = p.image });
      board.push(<Tile key={`${i}${j}`} num={i * i + j} image={image} />);
    }
  }
  return (
    <>
      <div id="pawn-promotion-modal" className='hidden' ref={pawnPromotionModalRef}>
        <div className='modal-body' >
          <img onClick={() => promotePawn("rook")} src={`/assets/images/${promotionTeamType()}_rook.png`} />
          <img onClick={() => promotePawn("queen")} src={`/assets/images/${promotionTeamType()}_queen.png`} />
          <img onClick={() => promotePawn("knight")} src={`/assets/images/${promotionTeamType()}_knight.png`} />
          <img onClick={() => promotePawn("bishop")} src={`/assets/images/${promotionTeamType()}_bishop.png`} />
        </div>

      </div>
      <div onPointerMove={e => movePiece(e)} onPointerDown={e => grabPiece(e)} onPointerUp={e => dropPiece(e)} id="gameboard" ref={chessBoardRef}>
        {board}
      </div>
      <div> <b>Number of Clients  = {numClients}</b></div>
      <div>
        <button ref={gameStartButtonRef} onClick={startGameHandler}>Start Game</button>
      </div>
    </>
  );
}
