  socket.on("colorMsg", (data) => {
    console.log((data));
    (data === "white") ? setIsBlackState(false) : setIsBlackState(true);
    (data === "white") ? setPieces(initialBoardStateWhite) : setPieces(initialBoardStateBlack);
    setGameState("started");
    sessionStorage.setItem(room, data);
  });


  // socket.on("youAreBlack", () => {
  //   setIsBlackState(true);
  //   setPieces(initialBoardStateBlack);
  //   setGameState("started");
  //   sessionStorage.setItem(room, "black");
  // });

  function startGameHandler() {
    socket.emit('startGameEvent', room);
    setTurn("WHITE");
  }

  function randomTestFunc() {
    const piece = pieces.find((p) => p.y === 1 && p.x === 7);
    console.log(piece);
    const bs = referee.getBoardStateAfterMove(7, 1, 0, 5, pieces);
    setPieces(bs);
    console.log(referee.inCheck(bs, piece.team, "", ""));
  }

  function grabPiece(e) {
    const chessboard = chessBoardRef.current;
    const element = (e.target);
    if (chessboard) {
      if (!element.classList.contains("chess-piece")) {
        const x = Math.floor((e.clientX - chessboard.offsetLeft) / GRID_SIZE);
        const y = Math.floor((e.clientY - chessboard.offsetTop) / GRID_SIZE);
        const a = dots.find((d) => d.i === x && d.j === y);
        if (a) {
          //here we make a normal move
          const updatedPieces = [];
          pieces.forEach((p) => {
            if (p === activePiece) {
              p.x = y;
              p.y = x;
              if ((0 === p.x || 7 === p.x) && p.type === "pawn") {
                setPromotionPawn(p);
                pawnPromotionModalRef.current.classList.remove('hidden');
              }
              updatedPieces.push(p);

            }
            else {
              updatedPieces.push(p);
            }
          });
          setPieces(updatedPieces);
          setDots([]);
          // sending update to the server after making the move 
          socket.emit("newMoveToServer", JSON.stringify({ pieces: updatedPieces, room: room, color: isBlackState ? "black" : "white" }));
          if (referee.inCheck(updatedPieces, activePiece.team === "our" ? "opponent" : "our") === true) {
            if (referee.inCheckMate(updatedPieces, activePiece.team === "our" ? "opponent" : "our") === true) {
              //event notifying that checkmate has been delivered by color
              socket.emit("checkMate", JSON.stringify({ room: room, color: isBlackState ? "black" : "white" }));
            }
          }
          else {
            if (referee.isStaleMate(updatedPieces, activePiece.team === "our" ? "opponent" : "our") === true) {
              //event notifying that it is a stalemate and game should be ended.
              socket.emit("stalemate", JSON.stringify({ room: room, color: isBlackState ? "black" : "white" }));
            }
          }
          setActivePiece(null);
        }
      }
      else {
        const x = Math.floor((e.clientX - chessboard.offsetLeft) / GRID_SIZE);
        const y = Math.floor((e.clientY - chessboard.offsetTop) / GRID_SIZE);
        const a = dots.find((d) => d.i === x && d.j === y);
        if (a) {
          //here we make a capture move
          const updatedPieces = [];
          pieces.forEach((p) => {
            if (p === activePiece) {
              const pawnDirection = p.team === "our" ? -1 : 1;
              p.x = a.j;
              p.y = a.i;
              if(p.type === "pawn" && a.j === p.x)
              {
                p.x+=pawnDirection;
              }
              if ((0 === p.x || 7 === p.x) && p.type === "pawn") {
                setPromotionPawn(p);
                pawnPromotionModalRef.current.classList.remove('hidden');
              }
              updatedPieces.push(p);
            }
            else {
              if (!(p.x === a.j && p.y === a.i)) {
                updatedPieces.push(p);
              }
            }
          });
          setPieces(updatedPieces);
          setDots([]);
          // sending update to the server after making the move 
          socket.emit("newMoveToServer", JSON.stringify({ pieces: updatedPieces, room: room, color: isBlackState ? "black" : "white" }));
          if (referee.inCheck(updatedPieces, activePiece.team === "our" ? "opponent" : "our") === true) {
            if (referee.inCheckMate(updatedPieces, activePiece.team === "our" ? "opponent" : "our") === true) {
              //event notifying that checkmate has been delivered by color
              socket.emit("checkMateDelivered", JSON.stringify({ room: room, color: isBlackState ? "black" : "white" }));
            }
          }
          else {
            if (referee.isStaleMate(updatedPieces, activePiece.team === "our" ? "opponent" : "our") === true) {
              //event notifying that it is a stalemate and game should be ended.
              socket.emit("stalemate", JSON.stringify({ room: room, color: isBlackState ? "black" : "white" }));
            }
          }
          setActivePiece(null);
        }
        else {
          const currentPiece = pieces.find(p => p.y === x && p.x === y);
          const dotsArr = [];
          for (let i = 0; i <= 7; ++i) {
            for (let j = 0; j <= 7; j++) {
              const a = referee.isValidMove(y, x, j, i, currentPiece.type, currentPiece.team, pieces, true);
              if (a) {
                const newBoardState = referee.getBoardStateAfterMove(currentPiece.x, currentPiece.y, i, j, pieces);
                if (!referee.inCheck(newBoardState, currentPiece.team)) {
                  dotsArr.push({ i: i, j: j });
                }
              }
            }
          }
          setDots(dotsArr);
          setActivePiece(currentPiece);
        }
      }
    }
  }

  // function movePiece(e) {
