import React,{useState} from 'react'
import { Navigate } from 'react-router-dom';
import GameBoard from './GameBoard';
import './GamePage.css'

export default function GamePage() {
    const CREATE_GAME_API_URL = "http://192.168.29.13:8080/api/create_game";
    const [roomId, setRoomId] = useState('');
    const [startButton,setStartButton] = useState(null);
    const [gamecontent,setGameContent] =useState(null)
    function startGameHandler() {
        return <Navigate to="/gameboard" room={data["room_id"]} isBlack={data["color"] === "black" ? true : false} />

        setStartButton(1);
        fetch(CREATE_GAME_API_URL, {
            method: "POST",
            headers: {
                "Content-type": "application/json",
            },
            body: "white"
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                // setRoomId(data["room_id"]);
                setGameContent(<GameBoard room={data["room_id"]} isBlack={data["color"] === "black" ? true : false} />)
            })
            .catch(error => console.error('Error:', error));
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        setStartButton(2);
        console.log(roomId);
        setGameContent(<GameBoard room={roomId} isBlack={false} />);
    };
    return (
        <div>
            <button onClick={startGameHandler}>Start Game</button>
            <h1 className='space' > <b>OR</b>  </h1>
            <form action="submit" onSubmit={handleSubmit} >
                <label htmlFor="roomId"> <b> <h1>Enter Room ID</h1> </b> </label>
                <input value={roomId} onChange={(e) => { setRoomId(e.target.value) }} type="text" placeholder='' id='roomId' name='roomId' />
                <button type="submit" method="post"><b>Go To Room</b></button>
            </form>
        </div>
    )
}
