import React,{useState} from 'react'
import { Navigate } from 'react-router-dom';
import GameBoard from './GameBoard';
import './GamePage.css'

export default function GamePage() {
    const CREATE_GAME_API_URL = "http://192.168.29.13:8080/api/create_game";
    const ROOM_API_URL = "http://192.168.29.13:8080/api/is_room_present";
    const [roomId, setRoomId] = useState('');
    const [gamecontent,setGameContent] =useState(null);
    const [startGameRoomId,setStartGameRoomId] = useState(null);
    const [navigateToRoom,setNavigateToRoom] = useState(null);
    const [invalidRoomID,setinvalidRoomID] = useState(null);
    function startGameHandler() {
        fetch(CREATE_GAME_API_URL, {
            method: "POST",
            headers: {
                "Content-type": "application/json",
            },
            body: "white"
        })
            .then(response => response.json())
            .then(data => {
                setStartGameRoomId(data["room_id"]);
                setGameContent(1);
            })
            .catch(error => console.error('Error:', error));
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(roomId);
        let isRoomPresent = false;
        fetch(ROOM_API_URL, {
            method: "POST",
            headers: {
                "Content-type": "application/json",
            },
            body: roomId
        })
            .then(response => response.json())
            .then(data => {
                isRoomPresent = data["is_room_present"] === "true" ? true : false;
                console.log(data["is_room_present"]);
            })
            .catch(error => console.error('Error:', error));
        if(isRoomPresent)
        {
            setinvalidRoomID(false);
            setNavigateToRoom(true);
        }
        else
        {
            setinvalidRoomID(true);
        }
    };
    return (
        <div>
            { !gamecontent &&  <button onClick={startGameHandler}>Start New Game</button>}
            {
                gamecontent &&
                <div>
                    Use below room ID to join your game !!! Also send this room ID to your opponent to join.
                    <br />
                    {startGameRoomId}
                </div>
            }
            <form action="submit" onSubmit={handleSubmit} >
                <label htmlFor="roomId"> <b> <h1>Enter Room ID</h1> </b> </label>
                <input value={roomId} onChange={(e) => { setRoomId(e.target.value) }} type="text" placeholder='' id='roomId' name='roomId' />
                <button type="submit" method="post"><b>Go To Room</b></button>
            </form>
            <>
                {
                    invalidRoomID === true && <div>Invalid Room ID !!!! Please retry with another room ID</div>
                }
            </>
        </div>
    )
}
